dashboardQuerySet.dashboardQuery:
xquery version "1.0"; 
declare namespace cda="urn:hl7-org:v3"; 
declare namespace xdbe="http://exist-db.org/xquery/xmldb";

declare variable $root := request:get-path-info();                    
let $count := count(xdbe:get-child-collections($root))

return
<queryResults>
<queryTime>{current-dateTime()}</queryTime>
<total value="{$count}"/>
<category displayName="Patients"/>
</queryResults>


xquery version "1.0"; 
declare namespace cda="urn:hl7-org:v3"; 

let $count := count(//cda:ClinicalDocument) 
return
<queryResults>
<queryTime>{current-dateTime()}</queryTime>
<total value="{$count}"/>
<category displayName="Documents"/>
</queryResults>


xquery version "1.0"; 
declare namespace cda="urn:hl7-org:v3";

let $formCount := count(collection()//cda:ClinicalDocument[contains(cda:typeId/@extension,'#CityEHR:Form')])
let $letterCount := count(collection()//cda:ClinicalDocument[contains(cda:typeId/@extension,'#CityEHR:Letter')])
let $notificationCount := count(collection()//cda:ClinicalDocument[contains(cda:typeId/@extension,'#CityEHR:Notification')])
let $pathwayCount := count(collection()//cda:ClinicalDocument[contains(cda:typeId/@extension,'#CityEHR:Pathway')])

return
<queryResults>
<queryTime>{current-dateTime()}</queryTime>
<total value="{sum(($formCount,$letterCount,$notificationCount,$pathwayCount))}"/>
<category displayName="Forms" value="{$formCount}"/>
<category displayName="Letters" value="{$letterCount}"/>
<category displayName="Notifications" value="{$notificationCount}"/>
<category displayName="Pathways" value="{$pathwayCount}"/>
</queryResults>


xquery version "1.0"; 

let $users := count(collection()/user)
let $loggedOff := count(collection()/user/audit/sessionInfo[@sessionId=''])

return
<queryResults>
<queryTime>{current-dateTime()}</queryTime>
<total value="{$users}"/>
<category displayName="Active" value="{$users - $loggedOff}"/>
<category displayName="Inactive" value="{$loggedOff}"/>
</queryResults>

xquery version "1.0";
let $careTeams := count(//careTeam)
return <queryResults> <queryTime>{current-dateTime()}</queryTime> <total value="{$careTeams}"/> <category displayName="Care teams"/> </queryResults>
